package er.art;import java.util.List;public class CoffeeMachine {    private final List<String> drinkList = List.of ( "ristretto", "espresso", "lungo", "cappuccino", "latte" );    private final List<String> commandList = List.of ( "water", "milk", "coffee" );    private int indexCoffee;    private int indexWater;    private int indexMilk;    public CoffeeMachine (String indexCoffee, String indexWater, String indexMilk) {        this.indexCoffee = Integer.parseInt ( indexCoffee );        this.indexWater = Integer.parseInt ( indexWater );        this.indexMilk = Integer.parseInt ( indexMilk );    }    public int getIndexCoffee () {        return indexCoffee;    }    public void setIndexCoffee (int indexCoffee) {        this.indexCoffee = indexCoffee;    }    public int getIndexWater () {        return indexWater;    }    public void setIndexWater (int indexWater) {        this.indexWater = indexWater;    }    public int getIndexMilk () {        return indexMilk;    }    public void setIndexMilk (int indexMilk) {        this.indexMilk = indexMilk;    }    public List<String> getDrinkList () {        return drinkList;    }    public List<String> getCommandList () {        return commandList;    }    public String getStartMsg () {        String msg = "\nПример команд:\n" +                     " • water N - залить N мл воды." +                     "\n" +                     " • milk N - залить N мл молока." +                     "\n" +                     " • coffee N - засыпать N мл кофе." +                     "\n" +                     " • latte N - приготовить латте на N мл. Приготовление происходит только при N ≥ 10." +                     "\n" +                     " • ristretto N - приготовить ристретто на N мл. Приготовление происходит только при N ≥ 10." +                     "\n" +                     " • cappuccino N - приготовить капучино на N мл. Приготовление происходит только при N ≥ 10." +                     "\n" +                     " • lungo N - приготовить лунго на N мл. Приготовление происходит только при N ≥ 10." +                     "\n" +                     " • espresso N - приготовить эспрессо на N мл. Приготовление происходит только при N ≥ 10." +                     "\n\n" +                     " • turn off - отключение. После получения команды отключения кофемашина должна завершить свою работу.";        return msg.trim ();    }    public String getValueCMachine () {        return String.format ( "В машине:\n" +                               " • %d мл. кофе\n" +                               " • %d мл. воды\n" +                               " • %d мл. молока\n\n", indexCoffee, indexWater, indexMilk ).trim ();    }    public void makeDrink (CoffeDrinks drink) {        drink.calkOfIngredients ();        System.out.println ( drink );    }}