package er.art;import java.util.List;public class CoffeeMachine {    private final List<String> drinkList = List.of ( "ristretto", "espresso", "lungo", "cappuccino", "latte" );    private final List<String> commandList = List.of ( "water", "milk", "coffee");    private int indexCoffee;    private int indexWater;    private int indexMilk;    public CoffeeMachine (String indexCoffee, String indexWater, String indexMilk) {        this.indexCoffee = Integer.parseInt ( indexCoffee );        this.indexWater = Integer.parseInt ( indexWater );        this.indexMilk = Integer.parseInt ( indexMilk );    }    public int getIndexCoffee () {        return indexCoffee;    }    public void setIndexCoffee (int indexCoffee) {        this.indexCoffee = indexCoffee;    }    public int getIndexWater () {        return indexWater;    }    public void setIndexWater (int indexWater) {        this.indexWater = indexWater;    }    public int getIndexMilk () {        return indexMilk;    }    public void setIndexMilk (int indexMilk) {        this.indexMilk = indexMilk;    }    public List<String> getDrinkList () {        return drinkList;    }    public List<String> getCommandList () {        return commandList;    }    public void makeDrink (CoffeDrinks drink) {        drink.calkOfIngredients ();        System.out.println ( drink );    }}