package er.art.coffeeDrinks;import er.art.CoffeDrinks;import er.art.CoffeeMachine;public class Ristretto implements CoffeDrinks {    private final int sizeDrink;    private final StringBuilder outStr = new StringBuilder ();    private final CoffeeMachine coffeeMachine;    boolean isTrueCalc = true;    private int water;    private int coffee;    public Ristretto (CoffeeMachine coffeeMachine, int sizeDrink) {        this.coffeeMachine = coffeeMachine;        this.sizeDrink = sizeDrink;        water = 0;        coffee = 0;    }    @Override    public String getName () {        return "Ristretto";    }    public void calkOfIngredients () {        water = (int) Math.round ( sizeDrink * 0.5d );        coffee = (int) Math.round ( sizeDrink * 0.5d );        int waterIndex = coffeeMachine.getIndexWater ();        int coffeeIndex = coffeeMachine.getIndexCoffee ();        int milkIndex = coffeeMachine.getIndexMilk ();        if (waterIndex < water | coffeeIndex < coffee) {            isTrueCalc = false;            outStr.append ( "Не хватает ингредиентов. Нужно пополнить запасы!" );        }        else {            coffeeMachine.setIndexWater ( waterIndex -= water );            coffeeMachine.setIndexCoffee ( coffeeIndex -= coffee );            outStr.append ( coffeeIndex ).append ( " " ).append ( waterIndex ).append ( " " ).append ( milkIndex );        }    }    @Override    public String toString () {        if (isTrueCalc)            return outStr.append ( " ---> (Ваш " ).append ( this.getName () ).append ( " готов)" ).toString ();        else return outStr.append ( "\n" + coffeeMachine.getValueCMachine () + "\n" ).toString ();    }}